import { Edit2, Plus, Trash2 } from 'lucide-react';
import React, { useState } from 'react';

interface CalendarEvent {
  id: string;
  title: string;
  start: Date;
  end: Date;
  description?: string;
  type: 'meeting' | 'task' | 'reminder' | 'other';
  color: string;
}

const eventTypes = [
  { label: '', value: 'meeting', color: '#3b82f6' },
  { label: '', value: 'task', color: '#10b981' },
  { label: '', value: 'reminder', color: '#f59e0b' },
  { label: '', value: 'other', color: '#6b7280' },
];

const CalendarManagement: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([
    {
      id: '1',
      title: '',
      start: new Date(2025, 9, 13, 10),
      end: new Date(2025, 9, 13, 11),
      type: 'meeting',
      color: '#3b82f6',
      description: '',
    },
    {
      id: '2',
      title: '',
      start: new Date(2025, 9, 15, 9),
      end: new Date(2025, 9, 15, 18),
      type: 'task',
      color: '#10b981',
      description: '',
    },
  ]);

  const [showModal, setShowModal] = useState(false);
  const [editingEvent, setEditingEvent] = useState<CalendarEvent | null>(null);
  const [formData, setFormData] = useState({
    title: '',
    startDate: '',
    startTime: '',
    endDate: '',
    endTime: '',
    description: '',
    type: 'other' as CalendarEvent['type'],
  });

  const [viewMode, setViewMode] = useState<'month' | 'day'>('month');
  const [showMeetings, setShowMeetings] = useState(true);
  const [showTasks, setShowTasks] = useState(true);
  const [showWeekends, setShowWeekends] = useState(true);

  const formatTime = (date: Date) =>
    date.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });
  const handlePrevMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() - 1),
    );
  const handleNextMonth = () =>
    setCurrentDate(
      new Date(currentDate.getFullYear(), currentDate.getMonth() + 1),
    );
  const handleToday = () => {
    const today = new Date();
    setCurrentDate(today);
    setSelectedDate(today);
  };

  const getDaysInMonth = (date: Date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const days: (Date | null)[] = [];

    for (let i = 0; i < firstDay.getDay(); i++) days.push(null);
    for (let i = 1; i <= lastDay.getDate(); i++)
      days.push(new Date(year, month, i));
    while (days.length % 7 !== 0) days.push(null);

    return days;
  };

  const getEventsForDate = (date: Date | null) => {
    if (!date) return [];
    let filteredEvents = events.filter(
      (event) => event.start.toDateString() === date.toDateString(),
    );

    if (!showMeetings) {
      filteredEvents = filteredEvents.filter((e) => e.type !== 'meeting');
    }
    if (!showTasks) {
      filteredEvents = filteredEvents.filter((e) => e.type !== 'task');
    }

    return filteredEvents;
  };

  const openCreateModal = () => {
    setEditingEvent(null);
    const now = selectedDate;
    setFormData({
      title: '',
      startDate: now.toISOString().split('T')[0],
      startTime: '09:00',
      endDate: now.toISOString().split('T')[0],
      endTime: '10:00',
      description: '',
      type: 'other',
    });
    setShowModal(true);
  };

  const openEditModal = (event: CalendarEvent) => {
    setEditingEvent(event);
    setFormData({
      title: event.title,
      startDate: event.start.toISOString().split('T')[0],
      startTime: formatTime(event.start),
      endDate: event.end.toISOString().split('T')[0],
      endTime: formatTime(event.end),
      description: event.description || '',
      type: event.type,
    });
    setShowModal(true);
  };

  const handleSave = () => {
    if (!formData.title.trim()) return alert('');
    const start = new Date(`${formData.startDate}T${formData.startTime}`);
    const end = new Date(`${formData.endDate}T${formData.endTime}`);
    if (end <= start) return alert('');

    const color =
      eventTypes.find((t) => t.value === formData.type)?.color || '#6b7280';

    if (editingEvent) {
      setEvents(
        events.map((e) =>
          e.id === editingEvent.id
            ? {
                ...e,
                title: formData.title,
                start,
                end,
                description: formData.description,
                type: formData.type,
                color,
              }
            : e,
        ),
      );
    } else {
      setEvents([
        ...events,
        {
          id: Date.now().toString(),
          title: formData.title,
          start,
          end,
          description: formData.description,
          type: formData.type,
          color,
        },
      ]);
    }
    setShowModal(false);
  };

  const handleDelete = (id: string) => {
    if (confirm('')) setEvents(events.filter((e) => e.id !== id));
  };

  const renderCalendarGrid = (
    days: (Date | null)[],
    isSmall: boolean = false,
  ) => {
    let filteredDays = days;
    let weekdays = ['', '', '', '', '', '', ''];

    if (!showWeekends) {
      filteredDays = [];
      weekdays = ['', '', '', '', ''];
      for (let i = 0; i < days.length; i++) {
        const weekdayIdx = i % 7;
        if (weekdayIdx !== 0 && weekdayIdx !== 6) {
          filteredDays.push(days[i]);
        }
      }
    }

    return (
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: `repeat(${weekdays.length}, 1fr)`,
          gap: '4px',
        }}
      >
        {weekdays.map((d) => (
          <div
            key={d}
            style={{
              textAlign: 'center',
              fontWeight: '600',
              fontSize: isSmall ? '11px' : '14px',
              padding: isSmall ? '4px 0' : '8px 0',
              color: '#4b5563',
            }}
          >
            {d}
          </div>
        ))}
        {filteredDays.map((day, idx) => {
          const dayEvents = day ? getEventsForDate(day) : [];
          const isToday =
            day && day.toDateString() === new Date().toDateString();
          const isSelected =
            day && day.toDateString() === selectedDate.toDateString();

          return (
            <div
              key={day ? day.toISOString() : `empty-${idx}`}
              onClick={() => day && setSelectedDate(day)}
              style={{
                minHeight: isSmall ? '28px' : '112px',
                border: '1px solid',
                borderColor: !day
                  ? '#e5e7eb'
                  : isSelected
                    ? '#60a5fa'
                    : isToday
                      ? '#fbbf24'
                      : '#e5e7eb',
                borderRadius: isSmall ? '4px' : '6px',
                padding: isSmall ? '2px' : '8px',
                cursor: 'pointer',
                display: 'flex',
                flexDirection: 'column',
                backgroundColor: !day
                  ? '#f9fafb'
                  : isSelected
                    ? '#eff6ff'
                    : isToday
                      ? '#fef3c7'
                      : '#ffffff',
              }}
              onMouseEnter={(e) => {
                if (day && !isSelected && !isToday) {
                  e.currentTarget.style.backgroundColor = '#f9fafb';
                }
              }}
              onMouseLeave={(e) => {
                if (day && !isSelected && !isToday) {
                  e.currentTarget.style.backgroundColor = '#ffffff';
                }
              }}
            >
              {day && (
                <>
                  <div
                    style={{
                      fontSize: isSmall ? '11px' : '14px',
                      fontWeight: '600',
                      marginBottom: '4px',
                      color: isToday
                        ? '#b45309'
                        : isSelected
                          ? '#1d4ed8'
                          : '#374151',
                    }}
                  >
                    {day.getDate()}
                  </div>
                  {!isSmall && (
                    <div
                      style={{
                        flex: 1,
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '4px',
                        overflow: 'hidden',
                      }}
                    >
                      {dayEvents.map((ev) => (
                        <div
                          key={ev.id}
                          style={{
                            backgroundColor: ev.color,
                            color: 'white',
                            padding: '4px 8px',
                            borderRadius: '4px',
                            fontSize: '12px',
                            cursor: 'pointer',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'space-between',
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            whiteSpace: 'nowrap',
                            transition: 'box-shadow 0.2s',
                          }}
                          onClick={(e) => {
                            e.stopPropagation();
                            openEditModal(ev);
                          }}
                          onMouseEnter={(e) => {
                            e.currentTarget.style.boxShadow =
                              '0 4px 6px -1px rgba(0, 0, 0, 0.1)';
                          }}
                          onMouseLeave={(e) => {
                            e.currentTarget.style.boxShadow = 'none';
                          }}
                        >
                          <span
                            style={{
                              overflow: 'hidden',
                              textOverflow: 'ellipsis',
                              flex: 1,
                            }}
                          >
                            {ev.title}
                          </span>
                          <span
                            style={{
                              fontSize: '10px',
                              opacity: 0.9,
                              marginLeft: '4px',
                              whiteSpace: 'nowrap',
                            }}
                          >
                            {formatTime(ev.start)}
                          </span>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}
            </div>
          );
        })}
      </div>
    );
  };

  const days = getDaysInMonth(currentDate);
  const monthName = currentDate.toLocaleDateString('zh-CN', {
    year: 'numeric',
    month: 'long',
  });

  return (
    <div
      style={{
        minHeight: '100vh',
        backgroundColor: '#f9fafb',
        padding: '24px',
