                  <span style={{ fontSize: '14px', color: '#374151' }}>

                  </span>
                </label>
                <label
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    cursor: 'pointer',
                  }}
                >
                  <input
                    type="checkbox"
                    checked={showTasks}
                    onChange={(e) => setShowTasks(e.target.checked)}
                    style={{ width: '16px', height: '16px' }}
                  />
                  <span style={{ fontSize: '14px', color: '#374151' }}>

                  </span>
                </label>
                <label
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: '8px',
                    cursor: 'pointer',
                  }}
                >
                  <input
                    type="checkbox"
                    checked={showWeekends}
                    onChange={(e) => setShowWeekends(e.target.checked)}
                    style={{ width: '16px', height: '16px' }}
                  />
                  <span style={{ fontSize: '14px', color: '#374151' }}>

                  </span>
                </label>
              </div>
            </div>

            {/*  */}
            <div
              style={{
                backgroundColor: 'white',
                borderRadius: '12px',
                boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
                padding: '12px',
                maxHeight: '300px',
                overflowY: 'auto',
              }}
            >
              <div
                style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  marginBottom: '8px',
                }}
              >
                <h2
                  style={{
                    fontSize: '14px',
                    fontWeight: '700',
                    color: '#1f2937',
                  }}
                >
                  {selectedDate.toLocaleDateString('zh-CN', {
                    month: 'long',
                    day: 'numeric',
                  })}
                </h2>
                <button
                  type="button"
                  onClick={openCreateModal}
                  style={{
                    padding: '4px 8px',
                    backgroundColor: '#2563eb',
                    color: 'white',
                    borderRadius: '8px',
                    fontSize: '12px',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px',
                    border: 'none',
                    cursor: 'pointer',
                  }}
                >
                  <Plus size={12} /> 
                </button>
              </div>
              {getEventsForDate(selectedDate).length === 0 ? (
                <p
                  style={{
                    color: '#9ca3af',
                    fontSize: '12px',
                    textAlign: 'center',
                    padding: '16px 0',
