import {
  AlignCenterOutlined,
  AlignLeftOutlined,
  AlignRightOutlined,
  BoldOutlined,
  CloudUploadOutlined,
  CopyOutlined,
  DeleteOutlined,
  DownloadOutlined,
  EditOutlined,
  FileTextOutlined,
  FontColorsOutlined,
  HighlightOutlined,
  ItalicOutlined,
  LeftOutlined,
  LinkOutlined,
  OrderedListOutlined,
  RedoOutlined,
  ReloadOutlined,
  RightOutlined,
  StrikethroughOutlined,
  TableOutlined,
  UnderlineOutlined,
  UndoOutlined,
  UnorderedListOutlined,
  UploadOutlined,
} from '@ant-design/icons';
import { PageContainer, ProCard } from '@ant-design/pro-components';
import {
  Button,
  Col,
  Input,
  List,
  Modal,
  message,
  Progress,
  Row,
  Select,
  Space,
  Spin,
  Tooltip,
  Typography,
  Upload,
} from 'antd';
import React, { useState } from 'react';
import { aiOptimizeDocument, aiWriteDocument } from '@/services/ai';
import { ossStorageService } from '@/services/ossStorage';

const { TextArea } = Input;
const { Title } = Typography;

interface SavedDocument {
  id: string;
  title: string;
  content: string;
  type: string;
  scenario?: string;
  url?: string;
  createdAt: Date;
  size?: number;
}

const DocumentWriter: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [prompt, setPrompt] = useState('');
  const [content, setContent] = useState('');
  const [htmlContent, setHtmlContent] = useState('');
  const [documentType, setDocumentType] = useState<string>('speech');
  const [scenario, setScenario] = useState<string>('');
  const [titleInput, setTitleInput] = useState('');
  const [lengthOption, setLengthOption] = useState<'short' | 'medium' | 'long'>(
    'medium',
  );
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [savedDocs, setSavedDocs] = useState<SavedDocument[]>([]);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [settingsPanelOpen, setSettingsPanelOpen] = useState(true);

  const scenarioOptions: Record<string, { label: string; value: string }[]> = {
    speech: [
      { label: '', value: 'opening' },
      { label: '', value: 'closing' },
    ],
    notice: [
      { label: '', value: 'internal' },
      { label: '', value: 'external' },
    ],
    report: [
      { label: '', value: 'personal' },
      { label: '', value: 'unit' },
      { label: '', value: 'special' },
    ],
    research: [
      { label: '', value: 'market' },
      { label: '', value: 'industry' },
    ],
    suggestion: [
      { label: '', value: 'policy' },
      { label: '', value: 'management' },
    ],
  };

  const formatContentToHTML = (text: string) => {
    if (!text) return '<p><br></p>';

    return text
      .split('\n')
      .map((line) => {
        const trimmed = line.trim();
        if (!trimmed) return '<p><br></p>';

        if (trimmed.startsWith('#')) {
          const level = (line.match(/^#+/) || [''])[0].length;
          const content = trimmed.replace(/^#+\s*/, '');
          if (level === 1) {
            return `<h1 style="text-align: center; font-size: 22px; font-weight: bold; margin: 24px 0 20px 0; color: #000; font-family: '', 'SimHei', sans-serif;">${content}</h1>`;
          } else if (level === 2) {
            return `<h2 style="font-size: 18px; font-weight: bold; margin: 20px 0 12px 0; color: #000; font-family: '', 'SimHei', sans-serif;">${content}</h2>`;
          } else {
            return `<h3 style="font-size: 16px; font-weight: bold; margin: 16px 0 10px 0; color: #000; font-family: '', 'SimHei', sans-serif;">${content}</h3>`;
          }
        }

        return `<p style="text-indent: 2em; line-height: 1.75; margin: 8px 0; font-size: 16px; color: #000;">${trimmed}</p>`;
      })
      .join('');
  };

  const handleGenerate = async () => {
    if (!prompt.trim()) {
      message.warning('');
      return;
    }
    setLoading(true);
    try {
      const filesContent = uploadedFiles
        .map((f) => `\n[: ${f.name}]`)
        .join('');
      const response = await aiWriteDocument({
        prompt: `${prompt}\n: ${documentType}\n: ${scenario}\n: ${lengthOption}${filesContent}`,
        documentType: documentType as any,
        tone: 'formal',
        language: 'zh-CN',
      });
      const generatedContent = response.data?.content || '';
      setContent(generatedContent);
      setHtmlContent(formatContentToHTML(generatedContent));
      message.success('');
    } catch (error) {
      message.error('');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleOptimize = async () => {
    if (!content.trim()) {
      message.warning('');
      return;
    }
    setLoading(true);
    try {
      const response = await aiOptimizeDocument({
        content,
        optimizationType: 'all',
      });
      const optimizedContent = response.data?.content || '';
      setContent(optimizedContent);
      setHtmlContent(formatContentToHTML(optimizedContent));
      message.success('');
    } catch (error) {
      message.error('');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleCopy = () => {
    const editor = document.getElementById('word-editor');
    if (editor) {
      const text = editor.innerText;
      navigator.clipboard.writeText(text);
      message.success('');
    }
  };

  const handleSaveToCloud = () => {
    if (!content.trim()) {
      message.warning('');
      return;
    }
    setShowSaveModal(true);
  };

  const handleConfirmSave = async () => {
    if (!titleInput.trim()) {
      message.warning('');
      return;
    }
    setLoading(true);
    setUploadProgress(0);
    try {
      const editor = document.getElementById('word-editor');
      const currentContent = editor?.innerText || content;
      const blob = new Blob([currentContent], { type: 'text/plain' });
      const file = new File([blob], `${titleInput}.txt`, {
        type: 'text/plain',
      });
      const result = await ossStorageService.uploadFile(file, {
        folder: 'documents',
        onProgress: (percent) => setUploadProgress(percent),
      });
      const newDoc: SavedDocument = {
        id: Date.now().toString(),
        title: titleInput,
        content: currentContent,
        type: documentType,
        scenario,
        url: result.url,
        createdAt: new Date(),
        size: result.size,
      };
      setSavedDocs([newDoc, ...savedDocs]);
      message.success('');
      setShowSaveModal(false);
      setTitleInput('');
    } catch (error: any) {
      message.error(error.message || '');
      console.error(error);
    } finally {
      setLoading(false);
      setUploadProgress(0);
    }
  };

  const handleDownload = (doc: SavedDocument) => {
    const blob = new Blob([doc.content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${doc.title}.txt`;
    a.click();
    URL.revokeObjectURL(url);
    message.success('');
  };

  const handleDelete = (docId: string) => {
    Modal.confirm({
      title: '',
      content: '',
      onOk: () => {
        setSavedDocs(savedDocs.filter((d) => d.id !== docId));
        message.success('');
      },
    });
  };

  const handleLoadDocument = (doc: SavedDocument) => {
    setContent(doc.content);
    setHtmlContent(formatContentToHTML(doc.content));
    setDocumentType(doc.type);
    setScenario(doc.scenario || '');
    message.success('');
  };

  const execCommand = (command: string, value?: string) => {
    document.execCommand(command, false, value);
  };

  const handleInput = (e: React.FormEvent<HTMLDivElement>) => {
    const text = e.currentTarget.innerText;
    setContent(text);
  };

  const insertTable = () => {
    const table =
      '<table border="1" style="border-collapse: collapse; width: 100%; margin: 12px 0; border: 1px solid #000;"><tr><td style="padding: 8px; border: 1px solid #000;">1</td><td style="padding: 8px; border: 1px solid #000;">2</td></tr><tr><td style="padding: 8px; border: 1px solid #000;">3</td><td style="padding: 8px; border: 1px solid #000;">4</td></tr></table>';
    execCommand('insertHTML', table);
  };

  return (
    <PageContainer
      header={{
        title: 'AI ',
        subTitle: '',
      }}
    >
      <div style={{ position: 'relative' }}>
        <Row gutter={[16, 16]}>
          {/*  */}
          <Col xs={24} lg={settingsPanelOpen ? 16 : 24}>
            <ProCard bordered>
              <Spin spinning={loading}>
                <Space
                  direction="vertical"
                  style={{ width: '100%' }}
                  size="middle"
                >
                  {/* Word  */}
                  <div
                    style={{
                      background: '#f3f4f6',
                      padding: '8px 12px',
                      borderRadius: '6px',
                      border: '1px solid #e5e7eb',
                      display: 'flex',
                      flexWrap: 'wrap',
                      gap: '6px',
                      alignItems: 'center',
                    }}
                  >
                    <Select
                      defaultValue="16px"
                      style={{ width: 85 }}
                      size="small"
                      onChange={(val) => execCommand('fontSize', val)}
                      options={[
                        { label: '12px', value: '2' },
                        { label: '14px', value: '3' },
                        { label: '16px', value: '4' },
                        { label: '18px', value: '5' },
                        { label: '20px', value: '6' },
                      ]}
                    />

                    <div
                      style={{
                        borderLeft: '1px solid #d1d5db',
                        height: '20px',
                        margin: '0 4px',
                      }}
                    />

                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<BoldOutlined />}
                        onClick={() => execCommand('bold')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<ItalicOutlined />}
                        onClick={() => execCommand('italic')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<UnderlineOutlined />}
                        onClick={() => execCommand('underline')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<StrikethroughOutlined />}
                        onClick={() => execCommand('strikeThrough')}
                      />
                    </Tooltip>

                    <div
                      style={{
                        borderLeft: '1px solid #d1d5db',
                        height: '20px',
                        margin: '0 4px',
                      }}
                    />

                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<FontColorsOutlined />}
                        onClick={() => {
                          const color = window.prompt(
                            'red  #ff0000',
                            '#000000',
                          );
                          if (color) execCommand('foreColor', color);
                        }}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<HighlightOutlined />}
                        onClick={() => execCommand('backColor', '#ffff00')}
                      />
                    </Tooltip>

                    <div
                      style={{
                        borderLeft: '1px solid #d1d5db',
                        height: '20px',
                        margin: '0 4px',
                      }}
                    />

                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<AlignLeftOutlined />}
                        onClick={() => execCommand('justifyLeft')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<AlignCenterOutlined />}
                        onClick={() => execCommand('justifyCenter')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<AlignRightOutlined />}
                        onClick={() => execCommand('justifyRight')}
                      />
                    </Tooltip>

                    <div
                      style={{
                        borderLeft: '1px solid #d1d5db',
                        height: '20px',
                        margin: '0 4px',
                      }}
                    />

                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<OrderedListOutlined />}
                        onClick={() => execCommand('insertOrderedList')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<UnorderedListOutlined />}
                        onClick={() => execCommand('insertUnorderedList')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<TableOutlined />}
                        onClick={insertTable}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<LinkOutlined />}
                        onClick={() => {
                          const url = window.prompt(':');
                          if (url) execCommand('createLink', url);
                        }}
                      />
                    </Tooltip>

                    <div
                      style={{
                        borderLeft: '1px solid #d1d5db',
                        height: '20px',
                        margin: '0 4px',
                      }}
                    />

                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<UndoOutlined />}
                        onClick={() => execCommand('undo')}
                      />
                    </Tooltip>
                    <Tooltip title="">
                      <Button
                        size="small"
                        icon={<RedoOutlined />}
                        onClick={() => execCommand('redo')}
                      />
                    </Tooltip>

                    <div style={{ flex: 1 }} />

                    <Space size="small">
                      <Button
                        size="small"
                        icon={<ReloadOutlined />}
                        onClick={handleOptimize}
                        disabled={!content}
                      >

                      </Button>
                      <Button
                        size="small"
                        icon={<CopyOutlined />}
                        onClick={handleCopy}
                        disabled={!content}
                      >

                      </Button>
                      <Button
                        size="small"
                        type="primary"
                        icon={<CloudUploadOutlined />}
                        onClick={handleSaveToCloud}
                        disabled={!content}
                      >

                      </Button>
                    </Space>
                  </div>

                  {/*  A4 Word  */}
                  <div
                    style={{
                      background: '#e5e5e5',
                      padding: '40px 20px',
                      borderRadius: '4px',
                      minHeight: '900px',
                      display: 'flex',
                      justifyContent: 'center',
                      alignItems: 'flex-start',
                    }}
                  >
                    <div
                      id="word-editor"
                      contentEditable
                      // biome-ignore lint/security/noDangerouslySetInnerHtml: 
                      dangerouslySetInnerHTML={{ __html: htmlContent }}
                      onInput={handleInput}
                      style={{
                        width: '21cm',
                        minHeight: '29.7cm',
                        background: '#ffffff',
                        padding: '2.54cm 3.18cm',
                        boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                        fontSize: '16px',
                        lineHeight: '1.75',
                        fontFamily:
                          '"Times New Roman", "", "FangSong", "SimSun", serif',
                        color: '#000',
                        outline: 'none',
                        wordWrap: 'break-word',
                        overflowWrap: 'break-word',
                      }}
                    />
                  </div>
                </Space>
              </Spin>
            </ProCard>
          </Col>

          {/*  */}
          {settingsPanelOpen && (
            <Col xs={24} lg={8}>
              <Space
                direction="vertical"
                style={{ width: '100%' }}
                size="middle"
              >
                {/*  */}
                <ProCard title="" bordered>
                  <Space
                    direction="vertical"
                    style={{ width: '100%' }}
                    size="middle"
                  >
                    <div>
                      <Title level={5}></Title>
                      <Select
                        value={documentType}
                        onChange={(val) => {
                          setDocumentType(val);
                          setScenario('');
                        }}
                        style={{ width: '100%' }}
                        options={[
                          { label: '', value: 'speech' },
                          { label: '', value: 'notice' },
                          { label: '', value: 'report' },
                          { label: '', value: 'research' },
                          { label: '', value: 'suggestion' },
                        ]}
                      />
                    </div>

                    <div>
                      <Title level={5}></Title>
                      <Select
                        value={scenario}
                        onChange={setScenario}
                        style={{ width: '100%' }}
                        placeholder=""
                        options={scenarioOptions[documentType] || []}
                      />
                    </div>

                    <div>
                      <Title level={5}></Title>
                      <Input
                        value={titleInput}
                        onChange={(e) => setTitleInput(e.target.value)}
                        placeholder=""
                        maxLength={100}
                      />
                    </div>

                    <div>
                      <Title level={5}></Title>
                      <Select
                        value={lengthOption}
                        onChange={setLengthOption}
                        style={{ width: '100%' }}
                        options={[
                          { label: ' (500)', value: 'short' },
                          { label: ' (1000)', value: 'medium' },
                          { label: ' (2000)', value: 'long' },
                        ]}
                      />
                    </div>

                    <div>
                      <Title level={5}></Title>
                      <Upload
                        beforeUpload={(file) => {
                          setUploadedFiles([...uploadedFiles, file]);
                          return false;
                        }}
                        multiple
                        fileList={uploadedFiles.map((f, idx) => ({
                          uid: `${f.name}-${idx}`,
                          name: f.name,
                          status: 'done' as const,
                        }))}
                        onRemove={(file) => {
                          const idx = uploadedFiles.findIndex(
                            (f, i) => `${f.name}-${i}` === file.uid,
                          );
                          if (idx > -1) {
                            const newFiles = [...uploadedFiles];
                            newFiles.splice(idx, 1);
                            setUploadedFiles(newFiles);
                          }
                        }}
                      >
                        <Button icon={<UploadOutlined />} block size="small">

                        </Button>
                      </Upload>
                    </div>

                    <div>
                      <Title level={5}></Title>
                      <TextArea
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        placeholder="..."
                        rows={5}
                        maxLength={2000}
                        showCount
                      />
                    </div>

                    <Button
                      type="primary"
                      icon={<EditOutlined />}
                      onClick={handleGenerate}
                      loading={loading}
                      block
                      size="large"
                    >

                    </Button>
                  </Space>
                </ProCard>

                {/*  */}
                <ProCard title="" bordered>
                  <List
                    dataSource={savedDocs}
                    locale={{ emptyText: '' }}
                    renderItem={(doc) => (
                      <List.Item
                        actions={[
                          <Button
                            key="load"
                            type="link"
                            size="small"
                            onClick={() => handleLoadDocument(doc)}
                          >

                          </Button>,
                          <Button
                            key="download"
                            type="link"
                            size="small"
                            icon={<DownloadOutlined />}
                            onClick={() => handleDownload(doc)}
                          />,
                          <Button
                            key="delete"
                            type="link"
                            size="small"
                            danger
                            icon={<DeleteOutlined />}
                            onClick={() => handleDelete(doc.id)}
                          />,
                        ]}
                      >
                        <List.Item.Meta
                          avatar={
                            <FileTextOutlined
                              style={{ fontSize: 20, color: '#1890ff' }}
                            />
                          }
                          title={doc.title}
                          description={
                            <Space direction="vertical" size={0}>
                              <span style={{ fontSize: '12px' }}>
                                : {doc.type}
                              </span>
                              {doc.scenario && (
                                <span style={{ fontSize: '12px' }}>
                                  : {doc.scenario}
                                </span>
                              )}
                              <span style={{ fontSize: '12px' }}>
                                {doc.createdAt.toLocaleDateString('zh-CN')}
                              </span>
                            </Space>
                          }
                        />
                      </List.Item>
                    )}
                  />
                </ProCard>
              </Space>
            </Col>
          )}
        </Row>

        {/*  */}
        <div
          style={{
            position: 'fixed',
            right: settingsPanelOpen
              ? 'calc((100vw - 1200px) / 2 + 400px + 16px)'
              : '20px',
            top: '50%',
            transform: 'translateY(-50%)',
            zIndex: 999,
            transition: 'right 0.3s ease',
          }}
        >
          <Tooltip
            title={settingsPanelOpen ? '' : ''}
            placement="left"
          >
            <Button
              type="primary"
              shape="circle"
              size="large"
              icon={settingsPanelOpen ? <RightOutlined /> : <LeftOutlined />}
              onClick={() => setSettingsPanelOpen(!settingsPanelOpen)}
              style={{
                boxShadow: '0 2px 8px rgba(0,0,0,0.15)',
                width: '48px',
                height: '48px',
              }}
            />
          </Tooltip>
        </div>
      </div>

      <Modal
        title=""
        open={showSaveModal}
        onOk={handleConfirmSave}
        onCancel={() => {
          setShowSaveModal(false);
          setTitleInput('');
        }}
        confirmLoading={loading}
      >
        <Space direction="vertical" style={{ width: '100%' }} size="large">
          <div>
            <Title level={5}></Title>
            <Input
              value={titleInput}
              onChange={(e) => setTitleInput(e.target.value)}
              placeholder=""
              maxLength={100}
            />
          </div>
          {uploadProgress > 0 && (
            <Progress percent={uploadProgress} status="active" />
          )}
        </Space>
      </Modal>
    </PageContainer>
  );
};

export default DocumentWriter;

