// Translator.tsx

import {
  CopyOutlined,
  DeleteOutlined,
  FileTextOutlined,
  HistoryOutlined,
  SoundOutlined,
  SwapOutlined,
} from '@ant-design/icons';
import { PageContainer, ProCard } from '@ant-design/pro-components';
import {
  Button,
  Col,
  Input,
  List,
  message,
  Progress,
  Row,
  Select,
  Space,
  Spin,
  Tabs,
  Tag,
  Typography,
  Upload,
} from 'antd';
import React, { useEffect, useState } from 'react';
import { baiduTranslateService } from '@/services/baiduTranslate';

const { TextArea } = Input;
const { Text } = Typography;

interface TranslationHistory {
  id: string;
  sourceText: string;
  translatedText: string;
  sourceLang: string;
  targetLang: string;
  timestamp: Date;
}

const Translator: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [sourceText, setSourceText] = useState('');
  const [translatedText, setTranslatedText] = useState('');
  const [sourceLang, setSourceLang] = useState('zh-CN');
  const [targetLang, setTargetLang] = useState('en-US');
  const [translationHistory, setTranslationHistory] = useState<
    TranslationHistory[]
  >([]);
  const [_charCount, setCharCount] = useState(0);

  const languages = [
    { label: '', value: 'zh-CN' },
    { label: '', value: 'en-US' },
    { label: '', value: 'ja-JP' },
    { label: '', value: 'ko-KR' },
    { label: '', value: 'fr-FR' },
    { label: '', value: 'de-DE' },
    { label: '', value: 'es-ES' },
    { label: '', value: 'ru-RU' },
  ];

  useEffect(() => {
    setCharCount(sourceText.length);
  }, [sourceText]);

  const handleTranslate = async () => {
    if (!sourceText.trim()) {
      message.warning('');
      return;
    }
    setLoading(true);
    try {
      const result = await baiduTranslateService.translate({
        text: sourceText,
        from: sourceLang,
        to: targetLang,
      });
      setTranslatedText(result);

      const newHistory: TranslationHistory = {
        id: Date.now().toString(),
        sourceText,
        translatedText: result,
        sourceLang,
        targetLang,
        timestamp: new Date(),
      };
      setTranslationHistory([newHistory, ...translationHistory.slice(0, 19)]);

      message.success('');
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  const handleSwapLanguages = () => {
    const tempLang = sourceLang;
    setSourceLang(targetLang);
    setTargetLang(tempLang);
    setSourceText(translatedText);
    setTranslatedText(sourceText);
  };

  const handleCopy = (text: string) => {
    navigator.clipboard.writeText(text);
    message.success('');
  };

  const handleSpeak = (text: string, lang: string) => {
    if ('speechSynthesis' in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      utterance.lang = lang;
      speechSynthesis.speak(utterance);
    } else {
      message.warning('');
    }
  };

  const handleClear = () => {
    setSourceText('');
    setTranslatedText('');
  };

  const handleLoadHistory = (history: TranslationHistory) => {
    setSourceText(history.sourceText);
    setTranslatedText(history.translatedText);
    setSourceLang(history.sourceLang);
    setTargetLang(history.targetLang);
  };

  const handleDeleteHistory = (id: string) => {
    setTranslationHistory(translationHistory.filter((h) => h.id !== id));
    message.success('');
  };

  const handleDocumentUpload = async (file: File) => {
    if (file.size > 5 * 1024 * 1024) {
      message.error(' 5MB');
      return false;
    }
    setLoading(true);
    try {
      const text = await file.text();
      setSourceText(text);
      message.success('...');
      setTimeout(() => handleTranslate(), 500);
    } catch {
      message.error('');
    } finally {
      setLoading(false);
    }
    return false;
  };

  return (
    <PageContainer
      header={{
        title: 'AI ',
        subTitle: '',
      }}
    >
      <ProCard>
        <Tabs
          items={[
            {
              key: 'text',
              label: '',
              children: (
                <Row gutter={[24, 24]}>
                  <Col span={24}>
                    <Space
                      size="large"
                      style={{ width: '100%', justifyContent: 'center' }}
                    >
                      <Select
                        value={sourceLang}
                        onChange={setSourceLang}
                        style={{ width: 150 }}
                        options={languages}
                      />
                      <Button
                        icon={<SwapOutlined />}
                        onClick={handleSwapLanguages}
                        shape="circle"
                      />
                      <Select
                        value={targetLang}
                        onChange={setTargetLang}
                        style={{ width: 150 }}
                        options={languages}
                      />
                    </Space>
                  </Col>

                  <Col xs={24} lg={12}>
                    <ProCard title="" bordered>
                      <TextArea
                        value={sourceText}
                        onChange={(e) => setSourceText(e.target.value)}
                        placeholder="..."
                        rows={12}
                        maxLength={5000}
                        showCount
                      />
                      <div style={{ marginTop: 16 }}>
                        <Button
                          type="primary"
                          onClick={handleTranslate}
                          loading={loading}
                          block
                          size="large"
                        >

                        </Button>
                        <Button
                          onClick={handleClear}
                          style={{ marginTop: 8 }}
                          block
                        >

                        </Button>
                        <Button
                          icon={<SoundOutlined />}
                          onClick={() => handleSpeak(sourceText, sourceLang)}
                          disabled={!sourceText}
                          style={{ marginTop: 8 }}
                          block
                        >

                        </Button>
                      </div>
                    </ProCard>
                  </Col>

                  <Col xs={24} lg={12}>
                    <ProCard title="" bordered>
                      <Spin spinning={loading}>
                        <TextArea
                          value={translatedText}
                          placeholder="..."
                          rows={12}
                          readOnly
                        />
                        <div style={{ marginTop: 16 }}>
                          <Space>
                            <Button
                              icon={<CopyOutlined />}
                              onClick={() => handleCopy(translatedText)}
                              disabled={!translatedText}
                            >

                            </Button>
                            <Button
                              icon={<SoundOutlined />}
                              onClick={() =>
                                handleSpeak(translatedText, targetLang)
                              }
                              disabled={!translatedText}
                            >

                            </Button>
                          </Space>
                        </div>
                      </Spin>
                    </ProCard>
                  </Col>

                  <Col span={24}>
                    <ProCard
                      title={
                        <Space>
                          <HistoryOutlined />
                          <span></span>
                          <Tag color="blue">{translationHistory.length}</Tag>
                        </Space>
                      }
                      bordered
                      collapsible
                      defaultCollapsed
                    >
                      <List
                        dataSource={translationHistory}
                        locale={{ emptyText: '' }}
                        renderItem={(item) => (
                          <List.Item
                            actions={[
                              <Button
                                key="load"
                                type="link"
                                size="small"
                                onClick={() => handleLoadHistory(item)}
                              >

                              </Button>,
                              <Button
                                key="delete"
                                type="link"
                                size="small"
                                danger
                                icon={<DeleteOutlined />}
                                onClick={() => handleDeleteHistory(item.id)}
                              />,
                            ]}
                          >
                            <List.Item.Meta
                              title={
                                <Space>
                                  <Tag color="blue">
                                    {
                                      languages.find(
                                        (l) => l.value === item.sourceLang,
                                      )?.label
                                    }
                                  </Tag>
                                  <SwapOutlined />
                                  <Tag color="green">
                                    {
                                      languages.find(
                                        (l) => l.value === item.targetLang,
                                      )?.label
                                    }
                                  </Tag>
                                </Space>
                              }
                              description={
                                <div>
                                  <Text type="secondary">
                                    {item.sourceText.substring(0, 50)}
                                    {item.sourceText.length > 50 ? '...' : ''}
                                  </Text>
                                  <br />
                                  <Text
                                    type="secondary"
                                    style={{ fontSize: 12 }}
                                  >
                                    {item.timestamp.toLocaleString('zh-CN')}
                                  </Text>
                                </div>
                              }
                            />
                          </List.Item>
                        )}
                      />
                    </ProCard>
                  </Col>
                </Row>
              ),
            },
            {
              key: 'document',
              label: '',
              children: (
                <div style={{ padding: '40px', textAlign: 'center' }}>
                  <Upload.Dragger
                    beforeUpload={handleDocumentUpload}
                    maxCount={1}
                    accept=".txt,.doc,.docx,.pdf,.md"
                  >
                    <p className="ant-upload-drag-icon">
                      <FileTextOutlined style={{ fontSize: 48 }} />
                    </p>
                    <p className="ant-upload-text"></p>
                    <p className="ant-upload-hint">
                       TXTMDDOCDOCXPDF 5MB
                    </p>
                  </Upload.Dragger>
                  {loading && (
                    <div style={{ marginTop: 24 }}>
                      <Progress percent={50} status="active" />
                      <Text type="secondary">...</Text>
                    </div>
                  )}
                </div>
              ),
            },
          ]}
        />
      </ProCard>
    </PageContainer>
  );
};

export default Translator;

