// MeetingAssistant.tsx -  AI  API

import {
  AudioOutlined,
  CalendarOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  CopyOutlined,
  DownloadOutlined,
  FileTextOutlined,
  LinkOutlined,
  PauseCircleOutlined,
  TeamOutlined,
  VideoCameraOutlined,
} from '@ant-design/icons';
import { PageContainer, ProCard, ProList } from '@ant-design/pro-components';
import {
  Alert,
  Avatar,
  Badge,
  Button,
  Col,
  DatePicker,
  Descriptions,
  Input,
  Modal,
  message,
  Row,
  Space,
  Spin,
  Tabs,
  Tag,
  Timeline,
  TimePicker,
  Typography,
} from 'antd';
import dayjs, { type Dayjs } from 'dayjs';
import React, { useEffect, useRef, useState } from 'react';
import { aiGenerateMeetingNotes } from '@/services/ai';
import { tencentMeetingService } from '@/services/tencentMeeting';

const { TextArea } = Input;
const { Title, Paragraph, Text } = Typography;

interface ActionItem {
  id: string;
  task: string;
  assignee: string;
  deadline: string;
  status: 'pending' | 'completed';
}

interface MeetingInfo {
  meeting_id: string;
  meeting_code: string;
  subject: string;
  join_url: string;
  start_time?: string;
  end_time?: string;
  status?: string;
}

const MeetingAssistant: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [recording, setRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [meetingSummary, setMeetingSummary] = useState('');
  const [actionItems, setActionItems] = useState<ActionItem[]>([]);
  const [decisions, setDecisions] = useState<string[]>([]);
  const [participants, setParticipants] = useState<string[]>([]);
  const [recordingDuration, setRecordingDuration] = useState(0);

  // 
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [meetingList, setMeetingList] = useState<MeetingInfo[]>([]);
  const [currentMeeting, setCurrentMeeting] = useState<MeetingInfo | null>(
    null,
  );
  const [meetingSubject, setMeetingSubject] = useState('');
  const [meetingDate, setMeetingDate] = useState<Dayjs>(dayjs());
  const [startTime, setStartTime] = useState<Dayjs>(dayjs());
  const [endTime, setEndTime] = useState<Dayjs>(dayjs().add(1, 'hour'));

  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // 
  useEffect(() => {
    loadMeetingList();
  }, []);

  // 
  useEffect(() => {
    if (recording) {
      timerRef.current = setInterval(() => {
        setRecordingDuration((prev) => prev + 1);
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
      setRecordingDuration(0);
    }
    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [recording]);

  const formatDuration = (seconds: number): string => {
    const hrs = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hrs.toString().padStart(2, '0')}:${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const loadMeetingList = async () => {
    setLoading(true);
    try {
      const meetings = await tencentMeetingService.getUserMeetings();
      setMeetingList(meetings);
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateMeeting = async (): Promise<void> => {
    if (!meetingSubject.trim()) {
      message.warning('');
      return; //  void
    }

    setLoading(true);
    try {
      const startDateTime = meetingDate
        .hour(startTime.hour())
        .minute(startTime.minute())
        .unix();
      const endDateTime = meetingDate
        .hour(endTime.hour())
        .minute(endTime.minute())
        .unix();

      const meetingInfo = await tencentMeetingService.createMeeting({
        subject: meetingSubject,
        type: 0,
        start_time: startDateTime.toString(),
        end_time: endDateTime.toString(),
        settings: {
          mute_enable_join: true,
          allow_unmute_self: true,
          auto_record_type: 'cloud',
        },
      });

      setCurrentMeeting(meetingInfo);
      setShowCreateModal(false);
      message.success('');
      loadMeetingList();

      Modal.info({
        title: '',
        width: 600,
        content: (
          <Descriptions column={1} bordered>
            <Descriptions.Item label="">
              {meetingInfo.subject}
            </Descriptions.Item>
            <Descriptions.Item label="">
              {meetingInfo.meeting_code}
            </Descriptions.Item>
            <Descriptions.Item label="">
              <Space>
                <a
                  href={meetingInfo.join_url}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {meetingInfo.join_url}
                </a>
                <Button
                  size="small"
                  icon={<CopyOutlined />}
                  onClick={() => {
                    navigator.clipboard.writeText(meetingInfo.join_url);
                    message.success('');
                  }}
                />
              </Space>
            </Descriptions.Item>
          </Descriptions>
        ),
      });
    } catch (error: any) {
      message.error(error.message || '');
    } finally {
      setLoading(false);
    }
  };

  const handleJoinMeeting = (meeting: MeetingInfo) => {
    window.open(meeting.join_url, '_blank');
    message.info('...');
  };

  const handleCancelMeeting = async (meetingId: string) => {
    Modal.confirm({
      title: '',
      content: '',
      onOk: async () => {
        setLoading(true);
        try {
          await tencentMeetingService.cancelMeeting(meetingId, '');
          message.success('');
          loadMeetingList();
        } catch (error: any) {
          message.error(error.message || '');
        } finally {
          setLoading(false);
        }
      },
    });
  };

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      const audioChunks: Blob[] = [];

      mediaRecorder.ondataavailable = (e) => audioChunks.push(e.data);
      mediaRecorder.onstop = async () => {
        message.success('...');
      };

      mediaRecorder.start();
      setRecording(true);
      message.success('...');
    } catch {
      message.error('');
    }
  };

  const handleStopRecording = () => {
    if (mediaRecorderRef.current && recording) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => {
        track.stop();
      });
      setRecording(false);
    }
  };

  const handleGenerateNotes = async (): Promise<void> => {
    if (!transcript.trim()) {
      message.warning('');
      return; //  void
    }

    setLoading(true);
    try {
      const response = await aiGenerateMeetingNotes({
        transcript,
        meetingType: 'general',
        participants,
      });
      const data = response.data;
      setMeetingSummary(data.summary || '');
      setActionItems(data.actionItems || []);
      setDecisions(data.decisions || []);
      message.success('');
    } catch {
      message.error('');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = () => {
    const content = `
# 
${meetingSummary}

# 
${currentMeeting ? `- ${currentMeeting.subject}\n- ${currentMeeting.meeting_code}\n- ${currentMeeting.start_time}` : ''}

# 
${participants.map((p) => `- ${p}`).join('\n')}

# 
${decisions.map((d, i) => `${i + 1}. ${d}`).join('\n')}

# 
${actionItems.map((item, i) => `${i + 1}. ${item.task} - ${item.assignee} - ${item.deadline}`).join('\n')}

# 
${transcript}
        `;

    const blob = new Blob([content], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `meeting-notes-${Date.now()}.md`;
    a.click();
    URL.revokeObjectURL(url);
    message.success('');
  };

  return (
    <PageContainer
      header={{
        title: 'AI ',
        subTitle: '',
      }}
    >
      <Row gutter={[16, 16]}>
        {/*  */}
        <Col span={24}>
          <ProCard
            title={
              <Space>
                <VideoCameraOutlined />

              </Space>
            }
            extra={
              <Button
                type="primary"
                icon={<CalendarOutlined />}
                onClick={() => setShowCreateModal(true)}
              >

              </Button>
            }
            bordered
          >
            <Spin spinning={loading}>
              {meetingList.length === 0 ? (
                <Alert
                  message=""
                  description=""
                  type="info"
                  showIcon
                />
              ) : (
                <ProList
                  rowKey="meeting_id"
                  dataSource={meetingList}
                  metas={{
                    title: { dataIndex: 'subject' },
                    description: {
                      render: (_, row) => `${row.meeting_code}`,
                    },
                    actions: {
                      render: (_, row) => [
                        <Button
                          key="join"
                          type="link"
                          icon={<LinkOutlined />}
                          onClick={() => handleJoinMeeting(row)}
                        >

                        </Button>,
                        <Button
                          key="cancel"
                          type="link"
                          danger
                          onClick={() => handleCancelMeeting(row.meeting_id)}
                        >

                        </Button>,
                      ],
                    },
                  }}
                />
              )}
            </Spin>
          </ProCard>
        </Col>

        {/*  */}
        <Col xs={24} lg={16}>
          <ProCard title="" bordered>
            <Space direction="vertical" style={{ width: '100%' }} size="large">
              <Space>
                <Button
                  type={recording ? 'default' : 'primary'}
                  danger={recording}
                  icon={recording ? <PauseCircleOutlined /> : <AudioOutlined />}
                  onClick={
                    recording ? handleStopRecording : handleStartRecording
                  }
                  size="large"
                >
                  {recording ? '' : ''}
                </Button>
                {recording && (
                  <Space>
                    <Badge status="processing" text="" />
                    <Text strong>{formatDuration(recordingDuration)}</Text>
                  </Space>
                )}
              </Space>

              <div>
                <Title level={5}> / </Title>
                <TextArea
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                  placeholder="..."
                  rows={12}
                  style={{ minHeight: '300px' }}
                />
              </div>

              <Space>
                <Button
                  type="primary"
                  icon={<FileTextOutlined />}
                  onClick={handleGenerateNotes}
                  loading={loading}
                  size="large"
                >

                </Button>
                <Button
                  icon={<DownloadOutlined />}
                  onClick={handleExport}
                  disabled={!meetingSummary}
                >

                </Button>
              </Space>
            </Space>
          </ProCard>
        </Col>

        {/*  &  */}
        <Col xs={24} lg={8}>
          <Space direction="vertical" style={{ width: '100%' }} size={16}>
            <ProCard title="" bordered>
              <Space direction="vertical" style={{ width: '100%' }}>
                {participants.length > 0 ? (
                  participants.map((name) => (
                    <div key={name}>
                      <Avatar size="small" icon={<TeamOutlined />} /> {name}
                    </div>
                  ))
                ) : (
                  <Text type="secondary"></Text>
                )}
                <Input
                  placeholder=""
                  onPressEnter={(e) => {
                    const value = e.currentTarget.value.trim();
                    if (value) {
                      setParticipants([...participants, value]);
                      e.currentTarget.value = '';
                    }
                  }}
                />
              </Space>
            </ProCard>
            <ProCard title="" bordered>
              <Space>
                <ClockCircleOutlined />
                <Text strong>{formatDuration(recordingDuration)}</Text>
              </Space>
            </ProCard>
          </Space>
        </Col>

        {/*  /  /  */}
        <Col span={24}>
          <ProCard>
            <Tabs
              items={[
                {
                  key: 'summary',
                  label: '',
                  children: (
                    <Spin spinning={loading}>
                      {meetingSummary ? (
                        <Paragraph>{meetingSummary}</Paragraph>
                      ) : (
                        <Text type="secondary"></Text>
                      )}
                    </Spin>
                  ),
                },
                {
                  key: 'actions',
                  label: (
                    <span>
                      {' '}
                      {actionItems.length > 0 && (
                        <Badge count={actionItems.length} />
                      )}
                    </span>
                  ),
                  children: (
                    <ProList<ActionItem>
                      rowKey="id"
                      dataSource={actionItems}
                      metas={{
                        title: { dataIndex: 'task' },
                        description: {
                          render: (_, row) => (
                            <Space>
                              <Text type="secondary">
                                {row.assignee}
                              </Text>
                              <Text type="secondary">{row.deadline}</Text>
                            </Space>
                          ),
                        },
                        actions: {
                          render: (_, row) => [
                            <Tag
                              key="status"
                              color={
                                row.status === 'completed'
                                  ? 'success'
                                  : 'default'
                              }
                            >
                              {row.status === 'completed' ? '' : ''}
                            </Tag>,
                          ],
                        },
                      }}
                      locale={{ emptyText: '' }}
                    />
                  ),
                },
                {
                  key: 'decisions',
                  label: (
                    <span>
                      {' '}
                      {decisions.length > 0 && (
                        <Badge count={decisions.length} />
                      )}
                    </span>
                  ),
                  children: (
                    <Timeline
                      items={
                        decisions.length > 0
                          ? decisions.map((d) => ({
                              children: d,
                              dot: <CheckCircleOutlined />,
                            }))
                          : [
                              {
                                children: (
                                  <Text type="secondary"></Text>
                                ),
                              },
                            ]
                      }
                    />
                  ),
                },
              ]}
            />
          </ProCard>
        </Col>
      </Row>

      {/*  */}
      <Modal
        title=""
        open={showCreateModal}
        onOk={handleCreateMeeting}
        onCancel={() => setShowCreateModal(false)}
        confirmLoading={loading}
        width={600}
      >
        <Space direction="vertical" style={{ width: '100%' }} size="large">
          <div>
            <Title level={5}> *</Title>
            <Input
              value={meetingSubject}
              onChange={(e) => setMeetingSubject(e.target.value)}
              placeholder=""
              maxLength={100}
            />
          </div>
          <div>
            <Title level={5}> *</Title>
            <DatePicker
              value={meetingDate}
              onChange={(date) => date && setMeetingDate(date)}
              style={{ width: '100%' }}
              format="YYYY-MM-DD"
            />
          </div>
          <Row gutter={16}>
            <Col span={12}>
              <Title level={5}> *</Title>
              <TimePicker
                value={startTime}
                onChange={(time) => time && setStartTime(time)}
                style={{ width: '100%' }}
                format="HH:mm"
              />
            </Col>
            <Col span={12}>
              <Title level={5}> *</Title>
              <TimePicker
                value={endTime}
                onChange={(time) => time && setEndTime(time)}
                style={{ width: '100%' }}
                format="HH:mm"
              />
            </Col>
          </Row>
          <Alert
            message=""
            description=""
            type="info"
            showIcon
          />
        </Space>
      </Modal>
    </PageContainer>
  );
};

export default MeetingAssistant;

